// SPDX-License-Identifier: MIT
pragma solidity 0.8.13;

import {MetaTxRequest} from "../structs/RequestTypes.sol";
import {ECDSA} from "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";

abstract contract GelatoMetaBoxBase {
    bytes32 public constant METATX_REQUEST_TYPEHASH =
        keccak256(
            bytes(
                // solhint-disable-next-line max-line-length
                "MetaTxRequest(uint256 chainId,address target,bytes data,address feeToken,uint256 paymentType,uint256 maxFee,address user,address sponsor,uint256 sponsorChainId,uint256 nonce,uint256 deadline,bool isEIP2771)"
            )
        );
    // solhint-disable-next-line max-line-length
    string public constant EIP712_DOMAIN_TYPE =
        "EIP712Domain(string name,string version,uint256 chainId,address verifyingContract)";

    function _getDomainSeparator(uint256 _chainId)
        internal
        view
        returns (bytes32)
    {
        return
            keccak256(
                abi.encode(
                    keccak256(bytes(EIP712_DOMAIN_TYPE)),
                    keccak256(bytes("GelatoMetaBox")),
                    keccak256(bytes("V1")),
                    bytes32(_chainId),
                    address(this)
                )
            );
    }

    function _verifyMetaTxRequestSignature(
        MetaTxRequest calldata _req,
        bytes calldata _signature,
        address _expectedSigner
    ) internal view {
        bytes32 domainSeparator = _getDomainSeparator(_req.chainId);

        bytes32 digest = keccak256(
            abi.encodePacked(
                "\x19\x01",
                domainSeparator,
                keccak256(_abiEncodeMetaTxRequest(_req))
            )
        );

        (address recovered, ECDSA.RecoverError error) = ECDSA.tryRecover(
            digest,
            _signature
        );
        require(
            error == ECDSA.RecoverError.NoError && recovered == _expectedSigner,
            "Invalid signature"
        );
    }

    function _isContract(address _account) internal view returns (bool) {
        return _account.code.length > 0;
    }

    function _abiEncodeMetaTxRequest(MetaTxRequest calldata _req)
        internal
        pure
        returns (bytes memory encodedReq)
    {
        encodedReq = abi.encode(
            METATX_REQUEST_TYPEHASH,
            _req.chainId,
            _req.target,
            keccak256(_req.data),
            _req.feeToken,
            _req.paymentType,
            _req.maxFee,
            _req.user,
            _req.sponsor,
            _req.sponsorChainId,
            _req.nonce,
            _req.deadline,
            _req.isEIP2771
        );
    }
}

contracts/structs/RequestTypes.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.13;

// Relay request with built-in MetaTx support with signature verification on behalf of user
// In case a sponsor (other than user) wants to pay for the tx,
// we will also need to verify sponsor's signature
struct MetaTxRequest {
    uint256 chainId;
    address target;
    bytes data;
    address feeToken;
    uint256 paymentType;
    uint256 maxFee;
    address user;
    address sponsor; // could be same as user
    uint256 sponsorChainId;
    uint256 nonce;
    uint256 deadline;
    bool isEIP2771;
}

// Similar to MetaTxRequest, but no need to implement user-specific signature verification logic
// Only sponsor signature is verified in order to ensure integrity of fee payments
struct ForwardRequest {
    uint256 chainId;
    address target;
    bytes data;
    address feeToken;
    uint256 paymentType;
    uint256 maxFee;
    address sponsor;
    uint256 sponsorChainId;
    uint256 nonce;
    bool enforceSponsorNonce;
}

