","keccak256":"0x374fe72e82bdb717b592560acf64f25fcefed78a7456f18e2f44ca4e6c6a4758","license":"MIT"},"contracts/structs/RequestTypes.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.15;

// Relay request with built-in MetaTx support with signature verification on behalf of user
// In case a sponsor (other than user) wants to pay for the tx,
// we will also need to verify sponsor's signature
struct MetaTxRequest {
    uint256 chainId;
    address target;
    bytes data;
    address feeToken;
    uint256 paymentType;
    uint256 maxFee;
    address user;
    address sponsor; // could be same as user
    uint256 sponsorChainId;
    uint256 nonce;
    uint256 deadline;
}

// Similar to MetaTxRequest, but no need to implement user-specific signature verification logic
// Only sponsor signature is verified in order to ensure integrity of fee payments
struct ForwardRequest {
    uint256 chainId;
    address target;
    bytes data;
    address feeToken;
    uint256 paymentType;
    uint256 maxFee;
    address sponsor;
    uint256 sponsorChainId;
    uint256 nonce;
    bool enforceSponsorNonce;
    bool enforceSponsorNonceOrdering;
}
","keccak256":"0xcc3a3385c5fd2f74db1928cc3c5461eb94ab51fa420390fbc0e1a915e35ef7bd","license":"MIT"},"contracts/utils/Utils.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.15;

import {NATIVE_TOKEN} from "../constants/Tokens.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {Address} from "@openzeppelin/contracts/utils/Address.sol";
import {
    SafeERC20
} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

// solhint-disable-next-line
function _transfer(
    address _token,
    address _to,
    uint256 _amount
) {
    if (_amount == 0) return;

    if (_token == NATIVE_TOKEN) {
        Address.sendValue(payable(_to), _amount);
    } else {
        SafeERC20.safeTransfer(IERC20(_token), _to, _amount);
    }
}

// solhint-disable-next-line
function _getBalance(address token, address user) view returns (uint256) {
    return token == NATIVE_TOKEN ? user.balance : IERC20(token).balanceOf(user);
}
